import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';

function Navbar() {
    const { user, logout } = useAuth();
    const { totalItems } = useCart();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/login');
    };
    
    const navLinkStyles = ({ isActive }) => ({
        fontWeight: isActive ? 'bold' : 'normal',
        color: isActive ? '#2563eb' : '#1f2937',
        marginRight: '1rem',
        textDecoration: 'none',
        position: 'relative'
    });

    return (
        <header className="bg-white shadow-md">
            <nav className="container mx-auto px-6 py-4 flex justify-between items-center">
                <NavLink to="/" className="text-2xl font-bold text-gray-800">
                    Automated Canteen
                </NavLink>
                <div className="flex items-center">
                    {user ? (
                        // This is the view for logged-in users
                        <>
                            {/* Customer-specific links */}
                            {user.role === 'customer' && (
                                <>
                                    <NavLink to="/" style={navLinkStyles}>Menu</NavLink>
                                    <NavLink to="/track" style={navLinkStyles}>Track Order</NavLink>
                                    <NavLink to="/cart" style={navLinkStyles}>
                                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 inline-block" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" />
                                        </svg>
                                        {totalItems > 0 && (
                                            <span className="absolute -top-2 -right-3 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                                                {totalItems}
                                            </span>
                                        )}
                                    </NavLink>
                                </>
                            )}
                            
                            {/* Kitchen-specific link */}
                            {user.role === 'kitchen' && <NavLink to="/kitchen" style={navLinkStyles}>Kitchen Dashboard</NavLink>}
                            
                            {/* Items visible to all logged-in users */}
                            <span className="text-gray-600 mx-4">|</span>
                            <span className="font-semibold mr-4">Welcome, {user.username}</span>
                            <button onClick={handleLogout} className="font-semibold text-red-600 hover:text-red-800">Logout</button>
                        </>
                    ) : (
                        // This is the view for non-logged-in users
                        <>
                            <NavLink to="/" style={navLinkStyles}>Menu</NavLink>
                            <NavLink to="/track" style={navLinkStyles}>Track Order</NavLink>
                            <NavLink to="/login" style={navLinkStyles}>Login</NavLink>
                        </>
                    )}
                </div>
            </nav>
        </header>
    );
}

export default Navbar;
