import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

function Navbar() {
    const { user, logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    return (
        <header className="bg-white shadow-md">
            <nav className="container mx-auto px-6 py-4 flex justify-between items-center">
                <div className="text-2xl font-bold text-gray-800">
                    Automated Canteen
                </div>
                <div>
                    {user ? (
                        <>
                            {user.role === 'customer' && <NavLink to="/" className="nav-link">Order Food</NavLink>}
                            {user.role === 'kitchen' && <NavLink to="/kitchen" className="nav-link">Kitchen Dashboard</NavLink>}
                            <NavLink to="/track" className="nav-link">Track Order</NavLink>
                            <button onClick={handleLogout} className="ml-4 font-semibold text-red-600 hover:text-red-800">Logout</button>
                            <span className="ml-4 font-semibold text-gray-600">Welcome, {user.username}</span>
                        </>
                    ) : (
                        <>
                            <NavLink to="/login" className="nav-link">Login</NavLink>
                            <NavLink to="/register" className="nav-link">Register</NavLink>
                        </>
                    )}
                </div>
            </nav>
        </header>
    );
}

export default Navbar;
