import React, { useState } from 'react';
// FIX: Added 'Navigate' to the import statement
import { useLocation, useNavigate, Navigate } from 'react-router-dom';
import axios from 'axios';
import { useCart } from '../context/CartContext';

function Payment() {
    const location = useLocation();
    const navigate = useNavigate();
    const { clearCart } = useCart();
    const { order } = location.state || {}; // Get order from navigation state
    const [isProcessing, setIsProcessing] = useState(false);
    const [paymentSuccess, setPaymentSuccess] = useState(false);

    if (!order) {
        // If there's no order, redirect back to the home page
        return <Navigate to="/" />;
    }

    const handleConfirmPayment = async () => {
        setIsProcessing(true);
        try {
            // Call the new backend endpoint to confirm payment
            await axios.put(`http://localhost:5001/api/orders/${order._id}/pay`);
            clearCart(); // Clear the cart after successful payment
            setPaymentSuccess(true);
        } catch (error) {
            console.error("Payment confirmation failed:", error);
            alert("There was an error processing your payment. Please try again.");
        } finally {
            setIsProcessing(false);
        }
    };

    if (paymentSuccess) {
        return (
            <div className="bg-white p-8 rounded-xl shadow-lg max-w-md mx-auto text-center">
                <h2 className="text-3xl font-bold text-green-600 mb-4">Payment Successful!</h2>
                <p className="text-gray-700 mb-2">Your order has been sent to the kitchen.</p>
                <p className="text-gray-700 mb-6">You can track your order with ID:</p>
                <div className="bg-gray-100 p-4 rounded-lg text-2xl font-mono font-bold text-blue-700 tracking-wider">
                    {order.orderId}
                </div>
                <button onClick={() => navigate('/track')} className="mt-8 bg-blue-600 text-white font-bold py-2 px-6 rounded-lg hover:bg-blue-700">
                    Track Order
                </button>
            </div>
        );
    }

    return (
        <div className="max-w-lg mx-auto bg-white p-8 rounded-xl shadow-lg">
            <h2 className="text-3xl font-bold text-gray-800 mb-6 text-center">Confirm Your Payment</h2>
            <div className="bg-gray-50 p-6 rounded-lg mb-6">
                <h3 className="text-xl font-semibold mb-4">Order Summary</h3>
                <ul className="space-y-2 mb-4">
                    {order.items.map((item, index) => (
                        <li key={index} className="flex justify-between">
                            <span>{item.quantity}x {item.name}</span>
                            <span>${(item.price * item.quantity).toFixed(2)}</span>
                        </li>
                    ))}
                </ul>
                <div className="border-t pt-4 flex justify-between font-bold text-xl">
                    <span>Total Amount:</span>
                    <span>${order.totalAmount.toFixed(2)}</span>
                </div>
            </div>
            {/* This is a mock payment form */}
            <div className="space-y-4">
                <div>
                    <label className="block text-sm font-medium text-gray-700">Card Number</label>
                    <input type="text" defaultValue="**** **** **** 4242" readOnly className="mt-1 block w-full bg-gray-100 p-2 border rounded-md" />
                </div>
                <div>
                    <label className="block text-sm font-medium text-gray-700">Card Holder</label>
                    <input type="text" defaultValue="John Doe" readOnly className="mt-1 block w-full bg-gray-100 p-2 border rounded-md" />
                </div>
                <button
                    onClick={handleConfirmPayment}
                    disabled={isProcessing}
                    className="w-full mt-6 bg-green-600 text-white font-bold py-3 px-4 rounded-lg hover:bg-green-700 transition-colors duration-300 disabled:bg-gray-400"
                >
                    {isProcessing ? 'Processing...' : `Pay $${order.totalAmount.toFixed(2)}`}
                </button>
            </div>
        </div>
    );
}

export default Payment;
